import React, { useState } from 'react';
import axios from 'axios';
import './Login.css'
import validation from './LoginValidation';
import { Link, useNavigate } from 'react-router-dom';

function Signup() {
    const [values, setValues] = useState({
        username: '',
        email: '',
        password: ''
    });
    const navigate = useNavigate();
    const [errors, setErrors] = useState({});

    const handleSubmit = (event) => {
        event.preventDefault();
        setErrors(validation(values));
        axios.post('http://localhost:3001/signup',{values})
        .then(result=>console.log(result))
        .catch(err=>console.log(err))
    };

    const handleInput = (event) => {
        const { name, value } = event.target;
        setValues(prev => ({ ...prev, [name]: value }));
    };

    return (
        <div className='signup'>
        <div className="form-box register">
                      <h2>Registration</h2>
                      <form onSubmit={handleSubmit} method="post">
                          <div className="input-box">
                              <span className="icon">
                                  <i className="fa-solid fa-user"></i>
                              </span>
                              <input value={values.username} onChange={handleInput} name='username' type="text" required />
                              <label>Username</label>
                          </div>
                          <div className="input-box">
                              <span className="icon">
                                  <i className="fa-solid fa-envelope"></i>
                              </span>
                              <input value={values.email} onChange={handleInput} type="email" required name="email"/>
                              <label>Email</label>
                          </div>
                          
                          <div className="input-box">
                              <span className="icon">
                
                                  <i className="fa fa-lock" aria-hidden="true"></i>
                              </span>
                              <input value={values.password} onChange={handleInput} type="password" required name="password"/>
                              <label>Password</label>
                          </div>
                          <div className="remember-forget">
                              <label><input type="checkbox"/>I agree to the terms & conditions</label>
                      </div>
                      <button type='submit'>Register</button>
                          {/* <Link to="/login" type="submit" className="register-btn">Register</Link> */}
                          <div className="login-register">
                              <p>Already have an account? <Link to="/login" className="login-link">Login</Link></p>
                          </div>
                      </form>
              </div>
              </div>
    );
}

export default Signup;
