import React, { useState } from 'react';
import axios from 'axios';
import './Login.css'
import validation from './LoginValidation';
import { Link, useNavigate } from 'react-router-dom';

function Signup() {
    const [username,setUsername]=useState()
    const [email,setEmail]=useState()
    const [password,setPassword]=useState()
    const navigate = useNavigate();
    const [errors, setErrors] = useState({});

    const handleSubmit = (event) => {
        event.preventDefault();
        axios.post('http://localhost:3000/signup',{username,email,password})
        .then(result=>console.log(result))
        .catch(err=>console.log(err))
    };

    

    return (
        <div className='signup'>
        <div className="form-box register">
                      <h2>Registration</h2>
                      <form onSubmit={handleSubmit} method="post">
                          <div className="input-box">
                              <span className="icon">
                                  <i className="fa-solid fa-user"></i>
                              </span>
                              <input value={username} name='username' onChange={(e)=>setUsername(e.target.value)} type="text" required />
                              <label>Username</label>
                          </div>
                          <div className="input-box">
                              <span className="icon">
                                  <i className="fa-solid fa-envelope"></i>
                              </span>
                              <input value={email} type="email" onChange={(e)=>setEmail(e.target.value)} required name="email"/>
                              <label>Email</label>
                          </div>
                          
                          <div className="input-box">
                              <span className="icon">
                
                                  <i className="fa fa-lock" aria-hidden="true"></i>
                              </span>
                              <input value={password} type="password" onChange={(e)=>setPassword(e.target.value)} required name="password"/>
                              <label>Password</label>
                          </div>
                          <div className="remember-forget">
                              <label><input type="checkbox"/>I agree to the terms & conditions</label>
                      </div>
                      <button type='submit'>Register</button>
                          {/* <Link to="/login" type="submit" className="register-btn">Register</Link> */}
                          <div className="login-register">
                              <p>Already have an account? <Link to="/login" className="login-link">Login</Link></p>
                          </div>
                      </form>
              </div>
              </div>
    );
}

export default Signup;
