import React, { useRef, useState } from 'react'
import {data} from './Data'
import './Test.css'
const Test = () => {
    let [index, setIndex]=useState(0)
    let [question,setQuestion]=useState(data[index])
    let [lock,setLock]=useState(false)

    let option1=useRef(null)
    let option2=useRef(null)
    let option3=useRef(null)
    let option4=useRef(null)

    let option_array=[option1,option2,option3,option4]
    const selected=(e,ans)=>{
        if(lock==false){e.target.classList.add('ans');setLock(true)}
        
    }

    const next=()=>{
        if(lock===true){
        setIndex(++index);
        setQuestion(data[index]);
        setLock(false);
        option_array.map((option)=>{
            option.current.classList.remove('ans')
            return null;
            })
        }
    } 

    const previous=()=>{
      
      setIndex(--index);
      setQuestion(data[index]);
      option_array.map((option)=>{
          option.current.classList.remove('ans')
          return null;
          })
      
  } 
  return (
    <div className='container'>
        hii
         <h1>Diabetes Prediction Test</h1>
         <hr/>
         <h2>{index+1}. {question.question}</h2>
         <ul>
            <li ref={option1} onClick={(e)=> selected(e,1)}>{question.option1}</li>
            <li ref={option2} onClick={(e)=> selected(e,2)}>{question.option2}</li>
         </ul>
         <button onClick={previous} className='previous'>Previous</button>
         <button onClick={next} className='next'>Next</button>
         <div className="index">{index+1} of {data.length} questions</div>
    </div>
  )
}

export default Test
