import React, { useState } from "react";
import "./Feedback.css";
import axios from "axios";

function Feedback() {
  const [feedback, setFeedback] = useState("");

  const handleSubmit = (event) => {
    event.preventDefault();
    console.log("Feedback submitted:", feedback);
    axios.post('http://localhost:3001/feedback',{feedback:feedback})
    .then("Data submitted")
    .catch(e=>console.log(e))
    setFeedback("");
  };

  const handleChange = (event) => {
    setFeedback(event.target.value);
  };

  return (
    <>
    <div className="feedback-container">
      <h1 className="feedback-title">Feedback</h1>
      <form onSubmit={handleSubmit}>
        <div className="form-group">
          <label htmlFor="feedbackTextarea" className="feedback-label">Your Feedback</label>
          <textarea
            className="form-control feedback-textarea"
            id="feedbackTextarea"
            rows="5"
            value={feedback}
            onChange={handleChange}
            required
          ></textarea>
        </div>
        <button type="submit" className="btn btn-primary feedback-submit-btn">
          Submit Feedback
        </button>
      </form>
    </div>
    </>
  );
}

export default Feedback;
