import React, { useState, useEffect } from "react";
import './Test.css'
import { Questions } from "./Questions";

function Test() {
  const [index, setIndex] = useState(0);
  const [question, setQuestion] = useState(Questions[index]);
  const [selectedAnswer, setSelectedAnswer] = useState(null);
  const [floatInput, setFloatInput] = useState(0);
  const [result, setResult] = useState(false);
  const [isAnswered, setIsAnswered] = useState(false); // Track if the question is answered

  useEffect(() => {
    setQuestion(Questions[index]);
    setIsAnswered(false);
    setSelectedAnswer(null); // Reset isAnswered state when the question changes
  }, [index]);

  useEffect(() => {
    // Set the index to 0 (first question) when the component mounts
    setIndex(0);
  }, []);

  const checkAns = (ans) => {
    setSelectedAnswer(ans);
    setIsAnswered(true);
  };

  const handleFloatInput = (e) => {
    setFloatInput(parseFloat(e.target.value));
    setIsAnswered(true); // Mark as answered when the float input changes
  };

  const reset = () => {
    setIndex(0);
    setSelectedAnswer(null);
    setFloatInput(0);
    setResult(false);
  };

  const next = () => {
    if (index === Questions.length - 1) {
      setResult(true);
      return;
    }
    setIndex(prevIndex => prevIndex + 1);
  };

  const prev = () => {
    if (index > 0) {
      setIndex(prevIndex => prevIndex - 1);
    }
  };

  return (
    <div className="container">
      <h1>Diabetes Prediction Test</h1>
      <hr />
      {result ? (
        <>
          <h2>Test is submitted successfully</h2>
          <button className="reset" onClick={reset}>Reset</button>
        </>
      ) : (
        <>
          {index === 0 ? (
            <>
              <h2>{index+1}. Specify your current weight:</h2>
              <input
                type="range"
                min={0}
                max={200}
                step={0.1}
                value={floatInput}
                onChange={handleFloatInput}
              />
              <p>Value: {floatInput.toFixed(1)}</p>
              <div className="index">{index+1} of {Questions.length} questions</div>
              <button className="next" onClick={next} disabled={!isAnswered}>Next</button>
            </>
          ) : (
            <>
              <h2>{index+1}. {question.question}</h2>
              <ul>
                {Object.keys(question).filter(key => key.startsWith("option")).map((option, i) => (
                  <li
                    key={option}
                    className={selectedAnswer === i + 1 ? "selected" : ""}
                    onClick={() => checkAns(i + 1)}
                  >
                    {question[option]}
                  </li>
                ))}
              </ul>
              <button className="prev" onClick={prev}>Previous</button>
              <button className="next" onClick={next} disabled={!isAnswered}>Next</button>
              <div className="index">{index+1} of {Questions.length} questions</div>
              {selectedAnswer !== null }
            </>
          )}
        </>
      )}
    </div>
  );
}

export default Test;
