import React, { useState, useEffect } from "react";
import './Test.css'
import { Questions } from "./Questions";

function Test() {
  const [index, setIndex] = useState(0);
  const [question, setQuestion] = useState(Questions[index]);
  const [textFieldValue, setTextFieldValue] = useState('');
  const [responses, setResponses] = useState([]);
  const [result, setResult] = useState(false);

  useEffect(() => {
    setQuestion(Questions[index]);
    setTextFieldValue(''); // Reset text field value when question changes
  }, [index]);

  const handleTextFieldChange = (e) => {
    setTextFieldValue(e.target.value);
  };

  const reset = () => {
    setIndex(0);
    setResponses([]);
    setResult(false);
  };

  const next = () => {
    if (index === Questions.length - 1) {
      setResult(true);
      // Send responses to Flask app
      fetch('http://127.0.0.1:5000/predict', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json'
        },
        body: JSON.stringify(responses)
      })
      .then(response => response.json())
      .then(predictions => {
        console.log('Predictions:', predictions);
        // Handle predictions
      })
      .catch(error => {
        console.error('Error:', error);
      });
      return;
    }
    setResponses(prevResponses => [...prevResponses, textFieldValue]);
    setIndex(prevIndex => prevIndex + 1);
  };

  const prev = () => {
    if (index > 0) {
      setIndex(prevIndex => prevIndex - 1);
    }
  };

  return (
    <div className="container">
      <h1>Diabetes Prediction Test</h1>
      <hr />
      {result ? (
        <>
          <h2>Test is submitted successfully</h2>
          <button className="reset" onClick={reset}>Reset</button>
        </>
      ) : (
        <>
          <h2>{index+1}. {question.question}</h2>
          <input
            type="text"
            value={textFieldValue}
            onChange={handleTextFieldChange}
          />
          <button className="prev" onClick={prev}>Previous</button>
          <button className="next" onClick={next}>Next</button>
          <div className="index">{index+1} of {Questions.length} questions</div>
        </>
      )}
    </div>
  );
}

export default Test;
