import React, { useState } from 'react';
import axios from 'axios';
import validation from './LoginValidation';
import { Link, useNavigate } from 'react-router-dom';

function Signup() {
    const [values, setValues] = useState({
        username: '',
        email: '',
        password: ''
    });
    const navigate = useNavigate();
    const [errors, setErrors] = useState({});

    const handleSubmit = (event) => {
        event.preventDefault();
        setErrors(validation(values));
        axios.post('http://localhost:8081/signup', values)
            .then(res => {
                navigate('/');
            })
            .catch(err => console.log(err));
    };

    const handleInput = (event) => {
        const { name, value } = event.target;
        setValues(prev => ({ ...prev, [name]: value }));
    };

    return (
        <div className='d-flex justify-content-center align-items-center bg-primary vh-100'>
            <div className='bg-white p-3 rounded w-25'>
                <form onSubmit={handleSubmit}>
                    <h2>Sign-up</h2>
                    <div className='mb-3'>
                        <label htmlFor='username'><strong>Username </strong></label>
                        <input type='text' name='username' placeholder='Enter Username' value={values.username} onChange={handleInput} className='form-control rounded-0' />
                    </div>
                    <div className='mb-3'>
                        <label htmlFor='email'><strong>Email </strong></label>
                        <input type='email' name='email' placeholder='Enter Email' value={values.email} onChange={handleInput} className='form-control rounded-0' />
                    </div>
                    <div className='mb-3'>
                        <label htmlFor='password'><strong>Password </strong></label>
                        <input type='password' name='password' placeholder='Enter Password' value={values.password} onChange={handleInput} className='form-control rounded-0' />
                    </div>
                    <button className='btn btn-success w-100 rounded-0' type='submit'><strong>Login</strong></button>
                    <p>I agree to terms & conditions</p>
                    <Link to="/" className='btn btn-default border w-100 bg-light rounded-0'>Create Account</Link>
                </form>
            </div>
        </div>
    );
}

export default Signup;
