import React, { useState, useEffect } from "react";
import './Test.css'
import { Questions } from "./Questions";
import { Link, useNavigate } from "react-router-dom";
import Result from "./Result";

function predictWithMLModel(inputData) {
  return fetch('http://127.0.0.1:5000/predict', {
      method: 'POST',
      headers: {
          'Content-Type': 'application/json'
      },
      body: JSON.stringify(inputData)
  })
  .then(response => response.json())
  .then(predictions => {
      console.log('Predictions: ', predictions);
      return predictions;
  })
  .catch(error => {
      console.error('Error:', error);
      return null;
  });
}

function Test() {
  const [predictions, setPredictions] = useState(null);
  const [index, setIndex] = useState(0);
  const [question, setQuestion] = useState(Questions[index]);
  const [heredity, setHeredity] = useState(null);
  const [age, setAge] = useState(0);
  const [physicalActivity, setPhysicalActivity] = useState(0);
  const [junk, setJunk] = useState(null);
  const [bp, setBp] = useState(null);
  const [glucose, setGlucose] = useState(0);
  const [bmi, setBmi] = useState(0);
  const [result, setResult] = useState(false);
  const [isAnswered, setIsAnswered] = useState(false);
  const [selectedAnswer, setSelectedAnswer] = useState(null);

   // useEffect(() => {
  //   const fetchData = async () => {
  //     const predictions = await predictWithMLModel(inputData);
  //     setPredictions(predictions);
  //   };
  //   fetchData();
  // }, []);

  useEffect(() => {
    setQuestion(Questions[index]);
    setIsAnswered(false);
    setSelectedAnswer(null);
  }, [index]);

  const checkAns = (ans) => {
    setSelectedAnswer(ans);
    setIsAnswered(true);

    switch (index) {
      case 1:
        setHeredity(ans);
        break;
      case 3:
        setJunk(ans);
        break;
      case 5:
        setBp(ans);
        break;
      default:
        break;
    }
  };

  const handleAgeChange = (e) => {
    setAge(parseFloat(e.target.value));
    setIsAnswered(true);
  };

  const handleGlucoseChange = (e) => {
    setGlucose(parseFloat(e.target.value));
    setIsAnswered(true);
  };

  const handleHeredityChange = (e) => {
    setHeredity((e.target.value));
    setIsAnswered(true);
  };

  const handlePhysicalActivity = (e) => {
    setPhysicalActivity(parseInt(e.target.value));
    setIsAnswered(true);
  };

  const handleJunk = (e) => {
    setJunk(parseFloat(e.target.value));
    setIsAnswered(true);
  };

  const handleBp = (e) => {
    setBp((e.target.value));
    setIsAnswered(true);
  };

  const handleBmiChange = (e) => {
    setBmi((e.target.value));
    setIsAnswered(true);
  }

  const reset = () => {
    setIndex(0);
    setSelectedAnswer(null);
    setAge(0);
    setBmi(0);
    setGlucose(0);
    setBp(false);
    setHeredity(false);
    setJunk(false);
    setPhysicalActivity(0);
    setResult(false);
  };

  const next = () => {
    if (index === Questions.length - 1) {
      setResult(true);
      return;
    }
    setIndex(prevIndex => prevIndex + 1);
    setAge(0);
    setBmi(0);
    setGlucose(0);
  };

  const navigate = useNavigate();
  const handleClick = () => {
    navigate('/')
  }

  return (
    <>
      <header>
        <h2 className="logo">GlucoWise</h2>
        <nav className="navigation">
          <Link to="/">Home</Link>
          <Link to="#">News</Link>
          <Link to="#">Feedback</Link>
          <Link to="#">About</Link>
          <button onClick={handleClick} className="loginbtn">Logout</button>
        </nav>
      </header>
      <div className="container">
        <h1>Diabetes Prediction Test</h1>
        <hr />
        {result ? (
          <>
            <h2>Test is submitted successfully</h2>
            <h2>with prob {predictions}</h2>
            <button className="reset" onClick={reset}>Reset</button>
            <Link to='/result' className="result">See Result</Link>
          </>
        ) : (
          <>
            <h2>{index + 1}. {question.question}</h2>
            {(index === 0) && (
              <>
                <input
                  className="slider"
                  type="range"
                  min={0}
                  max={100}
                  value={age}
                  onChange={handleAgeChange}
                />
                <p>Value: {age} years</p>
              </>
            )}
            {(index === 4) && (
              <>
                <input
                  className="slider"
                  type="range"
                  min={0}
                  max={300}
                  value={glucose}
                  onChange={handleGlucoseChange}
                />
                <p>Value: {glucose} mg/dL</p>
              </>
            )}
            {(index === 6) && (
              <>
                <input
                  className="slider"
                  type="range"
                  min={0}
                  step={0.1}
                  max={100}
                  value={bmi}
                  onChange={handleBmiChange}
                />
                <p>Value: {bmi} kg/m2</p>
              </>
            )}
            {(index === 2) && (
              <>
                <input
                  min={0}
                  type="number"
                  value={physicalActivity}
                  onChange={handlePhysicalActivity}
                />
                <p>Exercise Time: {physicalActivity} minutes</p>
              </>
            )}
            {index === 1 && (
              <ul>
                {Object.keys(question).filter(key => key.startsWith("option")).map((option, i) => (
                  <li
                    key={option}
                    className={selectedAnswer === i + 1 ? "selected" : ""}
                    onClick={() => checkAns(i + 1)}
                    onChange={handleHeredityChange}
                  >
                    {question[option]}
                  </li>
                ))}
              </ul>
            )}

            {index === 3 && (
              <ul>
                {Object.keys(question).filter(key => key.startsWith("option")).map((option, i) => (
                  <li
                    key={option}
                    className={selectedAnswer === i + 1 ? "selected" : ""}
                    onClick={() => checkAns(i + 1)}
                    onChange={handleJunk}
                  >
                    {question[option]}
                  </li>
                ))}
              </ul>
            )}

            {index === 5 && (
              <ul>
                {Object.keys(question).filter(key => key.startsWith("option")).map((option, i) => (
                  <li
                    key={option}
                    className={selectedAnswer === i + 1 ? "selected" : ""}
                    onClick={() => checkAns(i + 1)}
                    onChange={handleBp}
                  >
                    {question[option]}
                  </li>
                ))}
              </ul>
            )}

            {index !== 0 && (
              <button className="prev" onClick={() => setIndex(prevIndex => prevIndex - 1)}>Previous</button>
            )}
            <button className="next" onClick={next} disabled={!isAnswered}>Next</button>
            <div className="index">{index + 1} of {Questions.length} questions</div>
            {selectedAnswer !== null}
          </>
        )}
      </div>
    </>
  );
}

export default Test;
