import React, { useRef, useState, useEffect } from "react";
import './Test.css'
import { Questions } from "./Questions";

function Test() {
  const [index, setIndex] = useState(0);
  const [question, setQuestion] = useState(Questions[index]);
  const [lock, setLock] = useState(false);
  const [result, setResult] = useState(false);

  const optionRefs = useRef([]);

  useEffect(() => {
    setQuestion(Questions[index]);
  }, [index]);

  const checkAns = (e, ans) => {
    if (lock === false) {
      e.target.classList.add("selected");
      setLock(true);
      return null;
    }
    console.log(ans);
  }

  const reset = () => {
    setIndex(0);
    setLock(false);
    setResult(false);
    optionRefs.current = [];
  }

  const next = () => {
    if (lock === true) {
      if (index === Questions.length - 1) {
        setResult(true);
        return;
      }
      optionRefs.current.forEach(option => {
        if (option && option.classList) {
          option.classList.remove("selected");
        }
      });
      setIndex(prevIndex => prevIndex + 1);
      setLock(false);
    } else if (index === Questions.length - 1) {
      // If on the last question and an answer is not selected, 
      // prevent advancing to the result
      alert("Please select an answer before proceeding to the result.");
    }
  };
  
  

  const prev = () => {
    if (index > 0) {
      // Remove "selected" class from all options
      optionRefs.current.forEach(option => {
        if (option && option.classList) {
          option.classList.remove("selected");
        }
      });
      setIndex(prevIndex => prevIndex - 1);
      setLock(false);
    }
  };
  

  return (
    <div className="container">
      <h1>Diabetes Prediction Test</h1>
      <hr />
      {result ? (
        <>
          <h2>Test is submitted successfully</h2>
          <button className="reset" onClick={reset}>Reset</button>
        </>
      ) : (
        <>
          <h2>{index + 1}. {question.question}</h2>
          <ul>
            {Object.keys(question).filter(key => key.startsWith("option")).map((option, i) => (
              <li
                key={option}
                ref={el => optionRefs.current[i] = el}
                onClick={(e) => checkAns(e, i + 1)}
              >
                {question[option]}
              </li>
            ))}
          </ul>
          <button className="prev" onClick={prev} disabled={index === 0}>Previous</button>
          <button className="next" onClick={next} disabled={index === Questions.length - 1}>Next</button>
          <div className="index">{index + 1} of {Questions.length} questions</div>
        </>
      )}
    </div>
  )
}

export default Test;
