import React ,{useState} from 'react'
import axios from 'axios'
import validation from './LoginValidation'
import { Link, useNavigate } from 'react-router-dom'
function Signup() {
        const [values,setValues]=useState({
            username:'',
            email:'',
            password:''
        })
        const navigate=useNavigate();
        const [errors,setErrors]=useState({})
        const handleSubmit=(event)=>{
            event.preventDefault();
            setErrors(validation(values));
            axios.post('/localhost:8081/signup',values)
            .then(res=>{
                navigate('/');
            })
            .catch(err=>console.log(err))
        }
    
        const handleInput=(event)=>{
            setValues(prev=>({...prev,[event.target.name]:[event.target.values]}))
            
        }
  return (
    <div className='d-flex justify-content-center align-items-center bg-primary vh-100'>
        <div className='bg-white p-3 rounded w-25'>
            <form action='' onSubmit={handleSubmit}>
                <h2>Sign-up</h2>
            <div className='mb-3'>
                    <label htmlFor='username'><strong>Username </strong></label>
                    <input type='username' placeholder='Enter Username' onChange={handleInput} className='form-control rounded-0'/>
                </div>
                <div className='mb-3'>
                    <label htmlFor='email'><strong>Email </strong></label>
                    <input type='email' placeholder='Enter Email' className='form-control rounded-0'/>
                </div>
                <div className='mb-3'>
                    <label htmlFor='password'><strong>Password </strong></label>
                    <input type='password' placeholder='Enter Password' className='form-control rounded-0'/>
                </div>
                <button className='btn btn-success w-100 rounded-0'><strong>Login</strong></button>
                <p>I agree to terms & conditions</p>
                <Link to="/" className='btn btn-default border w-100 bg-light rounded-0'>Create Account</Link>
            </form>
        </div>
    </div>
  )
}

export default Signup
