import React, { useState } from 'react';
import './Login.css';
import Test from './Test';
import './DropdownList.css';
import { Link } from 'react-router-dom';

function Dashboard() {
  const [isOpen, setIsOpen] = useState(false);

  const toggleDropdown = () => {
    setIsOpen(!isOpen);
  };

  // Define an array of login options
  const loginOptions = [
    { label: 'Login as Patient', path: '/login' },
    { label: 'Login as Doctor', path: '/doctorLogin' }
  ];

  return (
    <div>
      <header>
        <h2 className="logo">Logo</h2>
        <nav className="navigation">
          <a href="/">Home</a>
          <a href="#">About</a>
          <a href="#">Services</a>
          <a href="#">Contact</a>
          <button className="loginbtn" onClick={toggleDropdown}>Login</button>
          
          {isOpen && (
            <div className="dropdown-content">
              {/* Map over the login options to generate the links */}
              {loginOptions.map((option, index) => (
                <Link key={index} className="drop" to={option.path}>{option.label}</Link>
              ))}
            </div>
          )}
        </nav>
      </header>
      <div>
        <Link to="/test" type="submit" className="quiz">Take the Test</Link>
      </div>
    </div>
  );
}

export default Dashboard;
