import React, { useState } from 'react'
import { Link } from 'react-router-dom'
import './Login.css'
import validation from './LoginValidation'
function Login() {
    const [values,setValues]=useState({
        email:'',
        password:''
    })
    const [errors,setErrors]=useState({

    })
    const handleSubmit=(event)=>{
        event.preventDefault();
        setErrors(validation(values));
    }

    const handleInput=(event)=>{
        setValues(prev=>({...prev,[event.target.name]:[event.target.values]}))
    }

const openPopup=()=>{
    const wrapper=document.querySelector('.wrapper')
    const btn=document.querySelector('.loginbtn');  
    btn.addEventListener('click', ()=>{
        wrapper.classList.add('btn-active')
    })
}

    return (

        <>
          <div>
              
      
      <div id="wrap" className="wrapper">
          <span className="icon-close"><i className="fa-solid fa-xmark"></i></span>
          <div className="form-box login">
              <h2>Login</h2>
              <form action="#" method="post">
                  <div className="input-box">
                      <span className="icon">
                          <i className="fa-solid fa-envelope"></i>
                      </span>
                      <input type="email" className="email" required/>
                      <label>Email</label>
                  </div>
                  <div className="input-box">
                      <span className="icon">
                          <i className="fa fa-lock" aria-hidden="true"></i>
                      </span>
                      <input type="password" className="password" required/>
                      <label>Password</label>
                  </div>
                  <div className="remember-forget">
                      <label><input type="checkbox"/>Remember me</label>
                      <a href="#">Forgot password?</a>
              </div>
                  <button type="submit" className="btn">Login</button>
                  <div className="login-register">
                      <p>Don't have an account? <Link to='/signup' id="reg" className="register-link">Register</Link></p>
                  </div>
              </form>
          </div>
          
      </div>
      </div>
      </>
        )
}

export default Login
